package ch09.zip_expanded;

public class tryForTest {
	static int i = 1;
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.print(i+" , ");
		m(i);
		System.out.println(i);
		System.out.print("Hello,\nworld!");


	}
	public static void m(int i)
	{
		i += 2;
	}
}


